import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Form from 'react-bootstrap/Form';
import background from './Home.module.scss';
import Sun from './Planets/Sun.js';
import Mercury from './Planets/Mercury';
import Venus from './Planets/Venus';
import Earth from './Planets/Earth';
import Mars from './Planets/Mars';
import Jupiter from './Planets/Jupiter';
import Saturn from './Planets/Saturn';
import Uranus from './Planets/Uranus';
import Neptune from './Planets/Neptune';

import { useState } from 'react';




const Home = () => {
    const [checked, setChecked] = useState(false);
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const mercuryShow = () => setShow(true);
    const venusShow = () => setShow(true);
    const earthShow = () => setShow(true);
    const marsShow = () => setShow(true);
    const jupiterShow = () => setShow(true);
    const saturnShow = () => setShow(true);
    const uranusShow = () => setShow(true);
    const neptuneShow = () => setShow(true);



    return (
    <>
        <div id={background.star1}></div>
        <div id={background.star2}></div>
        <div id={background.star3}></div>
        <div id={background.star4}></div>
        <Sun checked={checked} mercuryShow={mercuryShow}/>
        <Mercury checked={checked} show={show} handleClose={handleClose} mercuryShow={mercuryShow}/>
        <Venus checked={checked} show={show} handleClose={handleClose} venusShow={venusShow}/>
        <Earth checked={checked} show={show} handleClose={handleClose} earthShow={earthShow}/>
        <Mars checked={checked} show={show} handleClose={handleClose} marsShow={marsShow}/>
        <Jupiter checked={checked} show={show} handleClose={handleClose} jupiterShow={jupiterShow}/>
        <Saturn checked={checked} show={show} handleClose={handleClose} saturnShow={mercuryShow}/>
        <Uranus checked={checked} show={show} handleClose={handleClose} mercuryShow={mercuryShow}/>
        <Neptune checked={checked} show={show} handleClose={handleClose} mercuryShow={mercuryShow}/>

    <Form>
      <Form.Check 
        type="switch"
        id="custom-switch"
        className={background.switch}
        checked={checked}
        label={checked ? "애니메이션 모드" : "메뉴 모드" }
        onChange={(e) => setChecked(e.currentTarget.checked)} 
      />
      <svg className={ checked ? background.aniicon : background.menuicon}   width="30" height="30" clip-rule="evenodd" fill-rule="evenodd" stroke-linejoin="round" stroke-miterlimit="2" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path d="m11.998 21.995c5.517 0 9.997-4.48 9.997-9.997 0-5.518-4.48-9.998-9.997-9.998-5.518 0-9.998 4.48-9.998 9.998 0 5.517 4.48 9.997 9.998 9.997zm4.843-8.211c.108.141.157.3.157.456 0 .389-.306.755-.749.755h-8.501c-.445 0-.75-.367-.75-.755 0-.157.05-.316.159-.457 1.203-1.554 3.252-4.199 4.258-5.498.142-.184.36-.29.592-.29.23 0 .449.107.591.291z" fill-rule="nonzero"/></svg>
      <h5 className={ checked ? background.h5_ani : background.h5_menu} > 여기를 클릭해보세요! </h5>
    </Form>

    </>

    );
};

export default Home;
